sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
	if (newValue == '') {
      return;
	}

    var whatneeded = g_form.getValue("u_what_needed");
	g_form.clearOptions("u_what_needed");

	if(newValue == "human resource"){
		g_form.addOption("u_what_needed","human resource 1", "Human Resource 1");
		g_form.addOption("u_what_needed","human resource 2", "Human Resource 2");
	}

	if(newValue == "legal"){
		g_form.addOption("u_what_needed","legal 1", "Legal 1");
		g_form.addOption("u_what_needed","legal 2", "Legal 2");
	}

	if(newValue == "facilities"){
		g_form.addOption("u_what_needed","facilities 1", "Facility 1");
		g_form.addOption("u_what_needed","facilities 2", "Facility 2");
	}

	if(newValue == "your department choice"){
		g_form.addOption("u_what_needed","your department choice 1", "Your Department Choice 1");
		g_form.addOption("u_what_needed","your department choice 2", "Your Department Choice 2");
	}

	if(isLoading && !g_form.isNewRecord()){
			g_form.setValue('u_what_needed', whatneeded);
		}

}
